const express=require("express");const path=require("path");const app=express();app.use("/public",express.static(path.resolve(__dirname,"public")));app.get("/*",(e,s)=>{s.sendFile(path.resolve(__dirname,"public","index.html"))});app.listen(process.env.PORT||8080,()=>{console.log("Server started successfully! \n");console.log("HOST: localhost:8080 \n");console.log("Ctrl + C - it`s stop server\n")});